window.addEventListener("load",()=>{let t=document.getElementsByClassName("accordion__button");if(t.length)for(let e=0;e<t.length;e++)t[e].addEventListener("click",(function(){this.classList.toggle("active");let t=this.nextElementSibling;t.style.height?t.style.height=null:t.style.height=t.scrollHeight+"px"}))});